import{_ as t,p as e,q as n,a1 as o}from"./framework-5866ffd3.js";const a={},s=o('<p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"> Nutz为了提高开发效率做了很大的努力也非常成功。Nutz on Rails 只是站在巨人的肩膀上做点小小的工作。 </p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"> Nutz on Rails 规定了项目的一些开发规范，添加了COC特性(主要是为Nutz添加了一些默认设置) </p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"> 添加了Code 生成功能，还有已经在日程的scoffold 功能 </p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"> 未来还会集成jetty服务器，并会做一些定制，使开发时不需要再重启服务器,真正的热部署.还有使应用更方便配置，以及开发 , 测试 , 部署三种模式.方便开发和部署 </p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"> 还有至关重要的插件系统，没有插件的系统不是好系统，一个系统不可能应付所有的场景,插件系统必不可少. </p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"> 插件系统目前考虑思路，还没提到日程上，我会把我所有的空闲时间用到实现这些目标上的 :) </p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"><br></p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"> 注:<span style="background-color:#F9F9F9;font-family:Verdana, sans-serif, 宋体;font-size:14px;">nutz on rails完全兼容nutz的一切注解和规则，完全可以混合开发.</span></p><p style="color:#333333;font-family:微软雅黑, Verdana, sans-serif, 宋体;font-size:13px;text-align:left;background-color:#FFFFFF;text-indent:2em;"><span style="background-color:#F9F9F9;font-family:Verdana, sans-serif, 宋体;font-size:14px;">    你可以直接把nutz的包换成 nor的jar包，项目原有的功能完全正常，只需把新功能按照nutz on rails的约定进行开发就可以了</span></p>',9),l=[s];function r(i,f){return e(),n("div",null,l)}const c=t(a,[["render",r],["__file","2012-264.html.vue"]]);export{c as default};
